        (neg|troop_slot_eq, ":lords", slot_troop_occupation, dplmc_slto_dead),
        (troop_slot_eq, ":lords", slot_troop_culture, "fac_culture_7"),#Roman
        (store_faction_of_troop, ":fac", ":lords"),
